import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { FaSearch, FaShoppingCart, FaUser, FaStar } from 'react-icons/fa';
import './ProductDetails.css';

const ProductDetails = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState('description');
  const [product, setProduct] = useState(null);

  const addToCart = () => {
    const currentCart = JSON.parse(localStorage.getItem('cart') || '[]');
    const existingItem = currentCart.find(item => item.id === product.id);
    
    if (existingItem) {
      existingItem.quantity += 1;
      localStorage.setItem('cart', JSON.stringify(currentCart));
    } else {
      const newCart = [...currentCart, { ...product, quantity: 1 }];
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
    
    navigate('/cart');
  };

  useEffect(() => {
    // In a real app, you would fetch the product data from an API
    // For now, we'll get it from localStorage
    const selectedProduct = JSON.parse(localStorage.getItem('selectedProduct'));
    if (selectedProduct) {
      setProduct(selectedProduct);
    }
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const handleQuantityChange = (value) => {
    const newQuantity = Math.max(1, quantity + value);
    setQuantity(newQuantity);
  };

  const relatedProducts = [
    {
      id: 7,
      name: "Smart Watch X1",
      price: "$199.99",
      image: "https://images.unsplash.com/photo-1579586337278-3befd40fd17a?w=500&auto=format"
    },
    {
      id: 8,
      name: "Premium Smartphone",
      price: "$699.99",
      image: "https://images.unsplash.com/photo-1567581935884-3349723552ca?w=500&auto=format"
    },
    {
      id: 9,
      name: "Wireless Headphones",
      price: "$149.99",
      image: "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=500&auto=format"
    },
    {
      id: 10,
      name: "Tablet Pro",
      price: "$449.99",
      image: "https://images.unsplash.com/photo-1561154464-82e9adf32764?w=500&auto=format"
    },
    {
      id: 11,
      name: "Gaming Console",
      price: "$299.99",
      image: "https://images.unsplash.com/photo-1486401899868-0e435ed85128?w=500&auto=format"
    }
  ];

  return (
    <div className="product-details-page">
      <nav className="navbar">
        <div className="nav-left">
          <button className="back-button" onClick={() => navigate(-1)}>â€¹</button>
          <ul className="nav-links">
            <li><a onClick={() => navigate('/clothes')}>Clothes</a></li>
            <li><a onClick={() => navigate('/gadgets')}>Gadgets</a></li>
            <li><a onClick={() => navigate('/new')}>New</a></li>
            <li><a onClick={() => navigate('/featured')}>Featured</a></li>
            <li><a onClick={() => navigate('/barter')}>Barter</a></li>
          </ul>
        </div>

        <div className="nav-center">
          <h1>TECHSELL</h1>
        </div>

        <div className="nav-right">
          <button className="icon-button"><FaSearch /></button>
          <button className="icon-button"><FaShoppingCart /></button>
          <button className="icon-button"><FaUser /></button>
        </div>
      </nav>

      <div className="product-container">
        <div className="product-gallery">
          <div className="main-image">
            <img src={product.image} alt={product.name} />
          </div>
        </div>

        <div className="product-info">
          <h1>{product.name}</h1>
          <div className="rating">
            {[1, 2, 3, 4, 5].map((star) => (
              <FaStar key={star} className="star-filled" />
            ))}
          </div>
          <div className="price">{product.price}</div>
          
          <p className="description">{product.description}</p>

          <div className="quantity-selector">
            <button onClick={() => handleQuantityChange(-1)}>-</button>
            <input type="number" value={quantity} readOnly />
            <button onClick={() => handleQuantityChange(1)}>+</button>
          </div>

          <button className="add-to-cart-button" onClick={addToCart}>
            Add to Cart
          </button>
        </div>
      </div>

      <div className="related-products">
        <h2>Related Products</h2>
        <div className="products-grid">
          {relatedProducts.map(item => (
            <div 
              key={item.id} 
              className="product-card" 
              onClick={() => {
                localStorage.setItem('selectedProduct', JSON.stringify(item));
                navigate(`/product/${item.id}`);
              }}
            >
              <img src={item.image} alt={item.name} className="product-image" />
              <h3>{item.name}</h3>
              <p className="price">{item.price}</p>
              <button className="details-button">See Details</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
