import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaSearch, FaShoppingCart, FaUser, FaStar } from 'react-icons/fa';
import './ProductDetails.css';

const ProductDetails = () => {
  const navigate = useNavigate();
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState('description');
  const product = JSON.parse(localStorage.getItem('selectedProduct'));

  const relatedProducts = [
    {
      id: 1,
      name: "Classic White T-Shirt",
      price: "$29.99",
      image: "https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=500"
    },
    {
      id: 2,
      name: "Winter Coat",
      price: "$89.99",
      image: "https://images.unsplash.com/photo-1539533113208-f6df8cc8b543?w=500"
    },
    {
      id: 3,
      name: "Denim Jacket",
      price: "$69.99",
      image: "https://images.unsplash.com/photo-1523205771623-e0faa4d2813d?w=500"
    },
    {
      id: 4,
      name: "Casual Sneakers",
      price: "$59.99",
      image: "https://images.unsplash.com/photo-1549298916-b41d501d3772?w=500"
    },
    {
      id: 5,
      name: "Summer Dress",
      price: "$45.99",
      image: "https://images.unsplash.com/photo-1515372039744-b8f02a3ae446?w=500"
    }
  ];

  const handleRelatedProductClick = (product) => {
    localStorage.setItem('selectedProduct', JSON.stringify(product));
    navigate(`/product/${product.id}`);
  };

  return (
    <div className="product-page">
      <nav>
        <div className="nav-left">
          <button onClick={() => navigate(-1)}>â€¹</button>
          <a onClick={() => navigate('/clothes')}>Clothes</a>
          <a onClick={() => navigate('/gadgets')}>Gadgets</a>
          <a onClick={() => navigate('/new')}>New</a>
          <a onClick={() => navigate('/featured')}>Featured</a>
          <a onClick={() => navigate('/barter')}>Barter</a>
        </div>
        <div className="center-nav">TECHSELL</div>
        <div className="nav-right">
          <FaSearch />
          <FaShoppingCart />
          <FaUser />
        </div>
      </nav>

      <div className="product-main">
        <div className="product-image">
          <img src={product?.image} alt={product?.name} />
        </div>

        <div className="product-info">
          <h1>{product?.name}</h1>
          <div className="rating">
            {[...Array(5)].map((_, i) => (
              <FaStar key={i} className={i < 4 ? "star-filled" : "star-empty"} />
            ))}
            <span>(4.0)</span>
          </div>
          <div className="price">{product?.price}</div>

          <div className="quantity-wrapper">
            <div className="quantity-input">
              <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>-</button>
              <input type="number" value={quantity} readOnly />
              <button onClick={() => setQuantity(quantity + 1)}>+</button>
            </div>
            <button className="add-to-cart">Add to Cart</button>
          </div>

          <div className="product-meta">
            <div>Categories: <span>Clothing, Men's Shoes</span></div>
            <div>Tags: <span>nike</span></div>
            <div>Share product</div>
          </div>
        </div>
      </div>

      <div className="product-tabs">
        <div className="tab-header">
          <button 
            className={activeTab === 'description' ? 'active' : ''}
            onClick={() => setActiveTab('description')}
          >
            Description
          </button>
          <button 
            className={activeTab === 'reviews' ? 'active' : ''}
            onClick={() => setActiveTab('reviews')}
          >
            Reviews
          </button>
        </div>
        <div className="tab-content">
          <p>No description available.</p>
        </div>
      </div>

      <div className="related-products">
        <h2>Related Products</h2>
        <div className="products-grid">
          {relatedProducts.map((item) => (
            <div 
              key={item.id} 
              className="product-card"
              onClick={() => handleRelatedProductClick(item)}
            >
              <img src={item.image} alt={item.name} />
              <h3>{item.name}</h3>
              <span className="price">{item.price}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
