import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { FaSearch, FaShoppingCart, FaUser, FaStar } from 'react-icons/fa';
import './ProductDetails.css';

const ProductDetails = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState('description');
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const selectedProduct = JSON.parse(localStorage.getItem('selectedProduct'));
    if (selectedProduct) {
      setProduct(selectedProduct);
    }
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const handleQuantityChange = (value) => {
    const newQuantity = Math.max(1, quantity + value);
    setQuantity(newQuantity);
  };

  const addToCart = () => {
    const currentCart = JSON.parse(localStorage.getItem('cart') || '[]');
    const existingItem = currentCart.find(item => item.id === product.id);
    
    if (existingItem) {
      existingItem.quantity += quantity;
      localStorage.setItem('cart', JSON.stringify(currentCart));
    } else {
      const newCart = [...currentCart, { ...product, quantity }];
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
    
    navigate('/cart');
  };

  return (
    <div className="product-page">
      <nav className="navbar">
        <div className="nav-links">
          <a onClick={() => navigate('/clothes')}>Clothes</a>
          <a onClick={() => navigate('/gadgets')}>Gadgets</a>
          <a onClick={() => navigate('/new')}>New</a>
          <a onClick={() => navigate('/featured')}>Featured</a>
          <a onClick={() => navigate('/barter')}>Barter</a>
        </div>
        <div className="logo">TECHSELL</div>
        <div className="nav-icons">
          <FaSearch />
          <FaShoppingCart />
          <FaUser />
        </div>
      </nav>

      <button className="back-button" onClick={() => navigate(-1)}>‹</button>

      <div className="product-content">
        <div className="product-image">
          <img src={product.image} alt={product.name} />
          <div className="thumbnail-gallery">
            <button className="gallery-nav prev">‹</button>
            {[...Array(5)].map((_, i) => (
              <div key={i} className="thumbnail">
                <img src={product.image} alt={`Thumbnail ${i + 1}`} />
              </div>
            ))}
            <button className="gallery-nav next">›</button>
          </div>
        </div>

        <div className="product-details">
          <h1>{product.name}</h1>
          <div className="rating">
            {[...Array(5)].map((_, i) => (
              <FaStar key={i} className={i < 4 ? "star-filled" : "star-empty"} />
            ))}
            <span className="review-count">(4.0)</span>
          </div>
          <div className="price">${parseFloat(product.price.replace('$', '')).toFixed(2)}</div>
          
          <p className="description">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
          </p>

          <div className="quantity-control">
            <button onClick={() => handleQuantityChange(-1)}>-</button>
            <input type="number" value={quantity} readOnly />
            <button onClick={() => handleQuantityChange(1)}>+</button>
            <button className="add-to-cart" onClick={addToCart}>Add to Cart</button>
          </div>

          <div className="product-meta">
            <div>Categories: <span>Clothing, Men's Shoes</span></div>
            <div>Tags: <span>nike</span></div>
            <div>Share product</div>
          </div>
        </div>
      </div>

      <div className="product-tabs">
        <div className="tab-headers">
          <button 
            className={activeTab === 'description' ? 'active' : ''} 
            onClick={() => setActiveTab('description')}
          >
            Description
          </button>
          <button 
            className={activeTab === 'reviews' ? 'active' : ''} 
            onClick={() => setActiveTab('reviews')}
          >
            Reviews
          </button>
        </div>
        <div className="tab-content">
          {activeTab === 'description' ? (
            <div className="description-content">
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
              <p>Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
            </div>
          ) : (
            <div className="reviews-content">
              {/* Reviews content here */}
            </div>
          )}
        </div>
      </div>

      <div className="related-products">
        <h2>Related Products</h2>
        <div className="products-grid">
          {[...Array(5)].map((_, i) => (
            <div key={i} className="product-card">
              <img src={`https://via.placeholder.com/200x200`} alt={`Related Product ${i + 1}`} />
              <h3>Product Name</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
              <span className="price">$15.99</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
