import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaStar, FaSearch, FaShoppingCart, FaUser } from 'react-icons/fa';
import './ProductDetails.css';

const ProductDetails = () => {
  const navigate = useNavigate();
  const [quantity, setQuantity] = useState(1);
  const product = JSON.parse(localStorage.getItem('selectedProduct'));

  const handleAddToCart = () => {
    try {
      const cartItem = {
        id: product.id,
        name: product.name,
        price: product.price,
        image: product.image,
        quantity: quantity
      };
      
      const existingCart = JSON.parse(localStorage.getItem('cartItems')) || [];
      existingCart.push(cartItem);
      localStorage.setItem('cartItems', JSON.stringify(existingCart));
      
      alert('Item added to cart successfully!');
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0) {
      setQuantity(value);
    }
  };

  const handleCartClick = () => {
    console.log('Cart icon clicked');
    window.location.href = '/';
  };

  return (
    <div className="product-page">
      <nav className="top-nav">
        <div className="nav-left">
          <span className="back-arrow" onClick={() => window.location.href = '/'}>‹</span>
          <span>Clothes</span>
          <span>Gadgets</span>
          <span>New</span>
          <span>Featured</span>
          <span>Barter</span>
        </div>
        <div className="logo">TECHSELL</div>
        <div className="nav-right">
          <FaSearch className="nav-icon" />
          <FaShoppingCart 
            className="nav-icon" 
            onClick={handleCartClick} 
            style={{ cursor: 'pointer' }} 
          />
          <FaUser 
            className="nav-icon" 
            style={{ cursor: 'pointer' }} 
          />
        </div>
      </nav>

      <div className="product-container">
        <div className="product-left">
          <div className="main-image">
            <img src={product?.image} alt={product?.name} />
          </div>
          <div className="thumbnail-row">
            <button className="nav-btn prev">‹</button>
            <div className="thumbnails">
              {relatedProducts.slice(0, 5).map((item, index) => (
                <div key={index} className="thumbnail-box">
                  <img src={item.image} alt={item.name} />
                </div>
              ))}
            </div>
            <button className="nav-btn next">›</button>
          </div>
        </div>

        <div className="product-right">
          <h1>{product?.name}</h1>
          <div className="rating">
            <div className="stars">
              {[...Array(5)].map((_, i) => (
                <FaStar key={i} className={i < 4 ? "star-filled" : "star-empty"} />
              ))}
            </div>
            <span>(4.0)</span>
          </div>
          <div className="price">{product?.price}</div>
          
          <div className="quantity-section">
            <input 
              type="number" 
              value={quantity} 
              onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value)))}
              min="1"
            />
            <button 
              className="add-cart-btn"
              onClick={handleAddToCart}
            >
              Add to Cart
            </button>
          </div>

          <div className="meta-info">
            <p>Categories: <span>Clothing, Men's Shoes</span></p>
            <p>Tag: <span>nike</span></p>
            <p>Share product</p>
          </div>
        </div>
      </div>

      <div className="related-section">
        <h2>Related Products</h2>
        <div className="related-grid">
          {relatedProducts.map((item) => (
            <div 
              key={item.id} 
              className="related-item"
              onClick={() => {
                localStorage.setItem('selectedProduct', JSON.stringify(item));
                window.location.href = `/product/${item.id}`;
              }}
            >
              <img src={item.image} alt={item.name} />
              <h3>{item.name}</h3>
              <div className="price">{item.price}</div>
              <button className="see-details">See Details</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
