import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { FaStar, FaSearch, FaUser, FaShoppingCart } from 'react-icons/fa';
import './ProductDetails.css';

const ProductDetails = () => {
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const selectedProduct = JSON.parse(localStorage.getItem('selectedProduct'));
    if (selectedProduct) {
      setProduct(selectedProduct);
    }
  }, []);

  if (!product) return <div>Loading...</div>;

  return (
    <div className="product-page">
      <nav className="top-nav">
        <div className="nav-left">
          <a>Clothes</a>
          <a>Gadgets</a>
          <a>New</a>
          <a>Featured</a>
          <a>Barter</a>
        </div>
        <div className="logo">TECHSELL</div>
        <div className="nav-right">
          <FaSearch />
          <FaShoppingCart />
          <FaUser />
        </div>
      </nav>

      <button className="back-btn" onClick={() => navigate(-1)}>â€¹</button>

      <div className="main-content">
        <div className="product-image">
          <img src={product.image} alt={product.name} />
        </div>

        <div className="product-info">
          <h1>{product.name}</h1>
          <div className="rating">
            {[...Array(5)].map((_, i) => (
              <FaStar key={i} className={i < 4 ? "star-filled" : "star-empty"} />
            ))}
            <span>(4.0)</span>
          </div>
          <div className="price">{product.price}</div>

          <div className="quantity-section">
            <div className="quantity-control">
              <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>-</button>
              <input type="text" value={quantity} readOnly />
              <button onClick={() => setQuantity(quantity + 1)}>+</button>
            </div>
            <button className="add-cart-btn">Add to Cart</button>
          </div>

          <div className="product-meta">
            <p>Categories: <span>Clothing, Men's Shoes</span></p>
            <p>Tags: <span>nike</span></p>
            <p>Share product</p>
          </div>
        </div>
      </div>

      <div className="tabs">
        <div className="tab-header">
          <button className="active">Description</button>
          <button>Reviews</button>
        </div>
        <div className="tab-content">
          <p>No description available.</p>
        </div>
      </div>

      <section className="related-products">
        <h2>Related Products</h2>
        <div className="products-grid">
          {[1, 2, 3, 4, 5].map((item) => (
            <div key={item} className="product-card">
              <img src={`https://via.placeholder.com/200x200`} alt="Product" />
              <h3>Name</h3>
              <p className="price">$15</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default ProductDetails;
